import React from "react";
import {useRef} from "react";
import "./IntroductionPage.css";
import Navbar from "../Navbar/Navbar";
import Slider from "../Slider/Slider";
import maincombined from "../IMGS/maincombined.png"
import Button from "../Button/Button";

const IntoductionPage = () => {

  const about = useRef(null);
  const pricing = useRef(null);
  const contact = useRef(null);

  const scrollToSection = (elementRef) => {
    window.scrollTo({
      top: elementRef.current.offsetTop,
      behavior: 'smooth'
    })
  }

  return (
    <div>
      <div>
        <div className="one" id="home">
            <div className="containerbox">
                <div className="title">
                    <h1>Speak freely with Call App</h1>
                    <p>Master your language by talking with real people</p>
                    <Button>Try it Free</Button>
                </div>
                <div className="call">
                    <img src={maincombined}/>
                </div>
            </div>
        </div>
        <div className="two" id="about">
            <div className="intro">
                <h1> What is Call App</h1>
                <p>The Language Learning app where you create real communication with others by talking about your interest and hobbies</p> 
            </div>
            <div className="title">
                <button className="nostyle"><p>Chat</p></button>
                <button className="nostyle"><p>Audio</p></button>
                <button className="nostyle"><p>Video</p></button>
            </div>
            <Slider/>
            
        </div>
        <div className="three" id="pricing"></div>
        <div className="four" id="contact"></div>
        <div className="five"></div>
      </div>
    </div>
  );
};

export default IntoductionPage;
